import React, { useState } from 'react';
import { Button, Card, Form } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from '../../app store/slices/cartSlice';

function Product({ product }) {
  const [quantity, setQuantity] = useState(1);
  const dispatch = useDispatch();

  const handleIncrement = () => {
    setQuantity((prevQuantity) => prevQuantity + 1);
  };

  const handleDecrement = () => {
    if (quantity > 1) {
      setQuantity((prevQuantity) => prevQuantity - 1);
    }
  };
  const imageURL='data:image/jpeg;base64,'
  const cartItems = useSelector((state) => state.cart.cart);
  const existInCart = cartItems.findIndex((item) => item.productId === product.products.ROWID);

  const handleAddToCart = () => {
    const details = {
      productName: product.products.productName,
      productId: product.products.ROWID,
      price: product.products.price,
      quantity: quantity,
      subtotal: parseFloat(parseFloat(product.products.price) * parseInt(quantity)).toFixed(2),
    };
    dispatch(addToCart(details));
  };

  return (
    <Card className="m-2" style={{ width: '18rem' }}>
      {imageURL && <Card.Img variant="top" src={imageURL} />}
      <Card.Body className="d-flex flex-column justify-content-center align-items-center">
        <Card.Title>{product.products.productName}</Card.Title>
        <Card.Text>Price: Rs.{product.products.price}</Card.Text>
        <Form.Group className="d-flex flex-column justify-content-center align-items-center">
          <Form.Label>Quantity:</Form.Label>
          <div className="d-flex align-items-center">
            <Button variant="light" onClick={handleDecrement}>
              -
            </Button>
            <Form.Control
              type="number"
              value={quantity}
              className="form-control-sm"
              style={{ width: '50px' }}
              onChange={(e) => setQuantity(parseInt(e.target.value))}
            />
            <Button variant="light" onClick={handleIncrement}>
              +
            </Button>
          </div>
        </Form.Group>
        <Button variant="primary" onClick={handleAddToCart} disabled={existInCart !== -1 ? true :false}>
          Add to Cart
        </Button>
      </Card.Body>
    </Card>
  );
}

export default Product;
